import React, { useEffect } from "react";
import styled from "styled-components";
import { useRef } from "react";
import axios from "axios";
import { toast } from "react-toastify";
const FormContainer = styled.form`
  display: flex;
  align-items: flex-end;
  gap: 10px;
  flex-wrap: wrap;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
`;

const InputArea = styled.div`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  width: 120px;
  padding: 0 10px;
  border: 1px solid #bbb;
  border-radius: 5px;
  height: 40px;
`;

const Label = styled.label``;

const Button = styled.button`
  padding: 10px;
  cursor: pointer;
  border-radius: 5px;
  border: none;
  background-color: #2c73d2;
  color: white;
  height: 42px;
`;

const Form = ({ onEdit, setOnEdit, getFilms }) => {
  const ref = useRef();

  useEffect(() => {
    if (onEdit) {
      const film = ref.current;

      film.title.value = onEdit.title;
      film.description.value = onEdit.description;
      film.image_url.value = onEdit.image_url;
    }
  }, [onEdit]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const film = ref.current;

    if (
      !film.title.value ||
      !film.description.value ||
      !film.image_url.value ||
      !film.trailer_url.value
    ) {
      return toast.warn("Preencha todos os campos!");
    }
    if (onEdit) {
      await axios
        .put("http://localhost:8000/" + onEdit._id, {
          title: film.title.value,
          description: film.description.value,
          image_url: film.image_url.value,
          trailer_url: film.trailer_url.value,
        })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    } else {
      await axios
        .post("http://localhost:8000", {
          title: film.title.value,
          description: film.description.value,
          image_url: film.image_url.value,
          trailer_url: film.trailer_url.value,
        })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    }

    film.title.value = "";
    film.description.value = "";
    film.image_url.value = "";
    film.trailer_url.value = "";

    setOnEdit(null);
    getFilms();
  };

  return (
    <>
      <FormContainer ref={ref} onSubmit={handleSubmit}>
        <InputArea>
          <Label>Title</Label>
          <Input name="title" />
        </InputArea>
        <InputArea>
          <Label>Description</Label>
          <Input name="description" type="text" />
        </InputArea>
        <InputArea>
          <Label>Image URL</Label>
          <Input name="image_url" type="text" />
        </InputArea>
        <InputArea>
          <Label>Trailer URL</Label>
          <Input name="trailer_url" type="text" />
        </InputArea>

        <Button type="submit">Save</Button>
      </FormContainer>
    </>
  );
};

export default Form;
