import React from "react";
import styled from "styled-components";
import axios from "axios";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";
import "../styles/App.css";

const Table = styled.table`
  width: 90%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  word-break: break-all;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow-x: auto;
`;

export const Tbody = styled.tbody`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 90%;
  overflow-x: "scroll";
  margin-left: 52%;
`;

export const Tr = styled.tr`
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  height: 400px;
  width: 400px;
`;

export const Td = styled.td`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Grid = ({ films, setFilms, setOnEdit }) => {
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (_id) => {
    await axios
      .delete("http://localhost:8000/" + _id)
      .then(({ data }) => {
        const newArray = films.filter((film) => film._id !== _id);

        setFilms(newArray);
        toast.success(data);
      })
      .catch(({ data }) => toast.error(data));

    setOnEdit(null);
  };

  return (
    <>
      <Table>
        <Tbody>
          {films.map((item, i) => {
            return (
              <Tr style={{ border: "2px solid red" }} key={i}>
                <Td style={{ height: "5%", width: "150px" }}>{item.title}</Td>
                <Td style={{ height: "50%", width: "200px" }}>
                  <img src={item.image_url} alt={item.image_url} />{" "}
                </Td>
                <Td
                  style={{
                    height: "20%",
                    width: "200px",
                    textAlign: "center",
                    overflowY: "scroll",
                  }}
                >
                  {item.description}
                </Td>
                <Td>
                  <FaEdit
                    className="edit-hover"
                    onClick={() => handleEdit(item)}
                  />
                </Td>
                <Td>
                  <FaTrash
                    className="trash-hover"
                    onClick={() => handleDelete(item._id)}
                  />
                </Td>
              </Tr>
            );
          })}
        </Tbody>
      </Table>
    </>
  );
};

export default Grid;
