import React from "react";
import styled from "styled-components";
import axios from "axios";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";
import "../styles/App.css";
const Table = styled.table`
  width: 100%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  max-width: 1120px;
  margin: 20px auto;
  word-break: break-all;
  display: flex;
  overflow-x: auto;
`;

export const Thead = styled.thead``;

export const Tbody = styled.tbody`
  display: flex;
`;

export const Tr = styled.tr`
  display: flex;
  justify-content: center;
  flex-direction: column;
`;

export const Th = styled.th`
  text-align: center;
  border-bottom: inset;
  padding-bottom: 5px;

  @media (max-width: 500px) {
    ${(props) => props.onlyWeb && "display: none"}
  }
`;

export const Td = styled.td`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 50%;
`;

const Grid = ({ films, setFilms, setOnEdit }) => {
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (_id) => {
    await axios
      .delete("http://localhost:8000/" + _id)
      .then(({ data }) => {
        const newArray = films.filter((film) => film._id !== _id);

        setFilms(newArray);
        toast.success(data);
      })
      .catch(({ data }) => toast.error(data));

    setOnEdit(null);
  };

  return (
    <>
      <Table>
        <Thead>
          {/* <Tr>
            <Th>Title</Th>
            <Th onlyWeb>Description</Th>
            <Th>Image</Th>
            <Th>Trailer</Th>
          </Tr> */}
        </Thead>
        <Tbody>
          {films.map((item, i) => {
            return (
              <Tr key={i}>
                <Td width="30%">{item.title}</Td>
                <Td width="30%">{item.description}</Td>
                <Td width="30%">
                  <img src={item.image_url} alt={item.image_url} />{" "}
                </Td>
                <Td width="30%">
                  <iframe src={item.trailer_url} />
                </Td>
                <Td alignCenter width="5%">
                  <FaEdit
                    className="edit-hover"
                    onClick={() => handleEdit(item)}
                  />
                </Td>
                <Td alignCenter width="5%">
                  <FaTrash
                    className="trash-hover"
                    onClick={() => handleDelete(item._id)}
                  />
                </Td>
              </Tr>
            );
          })}
        </Tbody>
      </Table>
    </>
  );
};

export default Grid;
